public class IntOperations {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=Integer.parseInt(args[0]);
		int b=Integer.parseInt(args[1]);
		int ans=a+b;
		System.out.println(a+"+"+b+"="+ans);
	}

}
